<?php		//API Parameters	$apiInterface = "http://www.search.twitter.com/search.json?";	$searchTerm = urlencode($_GET['q']);	$todaysDate = date('Y') ."-". date('m') ."-". date('d');	/*************************		STEP ONE - GET DATA & SIMPLE COUNT		*************************/			//Get :) page one for now	$positiveResults = file_get_contents($apiInterface . "q=" . $searchTerm ."+%3A)"."&result_type=recent&rpp=100");	//Get :( page one for now	$negativeResults = file_get_contents($apiInterface . "q=" . $searchTerm ."+%3A("."&result_type=recent&rpp=100");	//Get Neutrals	$neutralResults = file_get_contents($apiInterface . "q=" . $searchTerm . "&result_type=recent");		//count the pos/neg	$pos = json_decode($positiveResults,true);	$neg = json_decode($negativeResults,true);	$neu = json_decode($neutralResults,true);		$positives = 0;	$negatives = 0;	$neutrals = 0;		for($i=0; $i < count($pos["results"]); $i++){		$positives++;	}	for($i=0; $i < count($neg["results"]); $i++){		$negatives++;	}	for($i=0; $i < count($neu["results"]); $i++){		$neutrals++;	}	/*************************		STEP TWO - CALCULATE PERCENTAGE		*************************/			//If prevents division by 0	if(($positives+$negatives)!=0){		$percentageOfPositives = ($positives / ($positives + $negatives)) * 100;		$percentageOfNegatives = 100 - $percentageOfPositives;	} else {		$percentageOfPositives = 50;		$percentageOfNegatives = 50;	}		/*************************		STEP TWO - CALCULATE PERCENTAGE		*************************/	//get the earliest latest times		$myDate = getTweetTime($pos["results"][0]["created_at"]);	echo "Recent: ".$myDate->format('Y-m-d h:i:s') . "<br/>";		$myDate = getTweetTime($pos["results"][(count($pos["results"]))-1]["created_at"]);	echo "Oldest: ".$myDate->format('Y-m-d h:i:s') . "<br/>";		if($positive == 100 || $negative == 100){		//for now we assume that 100 == a lot of pages		//we also assume that a query over 100 tweets will have a page 10		$earliestPos = getTweetTime($pos["results"][0]["created_at"]);	}			function getTweetTime($date){			return new DateTime($date);		}		/*************************		STEP TWO - RETURN DATA		*************************/	echo "Queried: ".$searchTerm . "<br/>";	echo "Positive: ".$positives . " ".round($percentageOfPositives)."<br/>";	echo "Negative: ".$negatives . " ".round($percentageOfNegatives)."<br/>";	echo "Neutrals: ".$neutrals . "<br/>";?>